                    //secPlanNum
                    percentage = GetPercent(PIn.Int(procTable.Rows[i][3].ToString()), PIn.Int(procTable.Rows[i][1].ToString()), PIn.Int(procTable.Rows[i][2].ToString()), PIn.String(procTable.Rows[i][5].ToString()), "pri");
