            command = "INSERT INTO programproperty (ProgramNum,PropertyDesc,PropertyValue" + ") VALUES(" + "'" + programNum.ToString() + "', " + "'Enter 0 to use PatientNum, or 1 to use ChartNum', " + "'0')";
