            {
                command = "DELETE FROM covpat WHERE CovPatNum=" + table.Rows[i][0].ToString();
                Db.nonQ32(command);
            }
            //Add a CovCat for Db------------------------------------------------------------------------------
            command = "UPDATE covcat SET CovOrder=CovOrder+1";
            //Move all other covcats down one in order
            Db.nonQ32(command);
